		[10020] = {10020, 8, 1, 1, 10019, 7536, "<font color='f4e6d5' size='19' opacity='255'>与<font color='00ff0d' size='19' opacity='255'>陈队长</font>对话</font>", "铁甲尸王？什么鬼，矿洞里的人常年不见阳光，估计头脑不清醒。我需要矿工亲自告诉我。", "<font color='f4e6d5' size='19' opacity='255'>与<font color='00ff0d' size='19' opacity='255'>矿工小李</font>对话</font>", "", "", "", 0, 0, 0, 0, 7539, "怎么又是你？这里的骷髅很多啊", "<font color='f4e6d5' size='19' opacity='255'>与<font color='00ff0d' size='19' opacity='255'>矿工小李</font>对话</font>", {{110057,0,1597}}, {{110057,0,1597}}, {{110057,0,1597}}, {}, 0, "<font color='fbfb9e' size='19' opacity='255'>[主线] 不相信</font><br/>", "", 0, 0, "", 0},
